import React, { useEffect, useState } from "react";
import "../../styles/buttons/RouteSwitch.css";
import { Link, useLocation } from "react-router-dom";
import { motion } from "framer-motion";

const RouteSwitch = () => {
  const location = useLocation();
  const { pathname } = location;

  const [chosenPath, setChosenPath] = useState(pathname);

  useEffect(() => {
    setChosenPath(pathname);

    console.log(chosenPath);
  }, [pathname]);

  return (
    <div id="switch-container">
      <Link
        to="/"
        className={`switch-button ${chosenPath === "/" ? "active" : ""}`}
      >
        Swap
      </Link>
      <Link
        to="/claim"
        className={`switch-button ${chosenPath === "/claim" ? "active" : ""}`}
      >
        Claim
      </Link>
      <motion.div
        id="button-bg"
        initial={{ x: pathname === "/" ? 0 : "100%" }}
        animate={{ x: pathname === "/" ? 0 : "100%" }}
        transition={{ bounce: 0 }}
      />
    </div>
  );
};

export default RouteSwitch;
