import { getBalanceAbi } from "../lib/abi/ierc20";
import Web3 from "web3";
const rpcURL = "https://kovan.infura.io/v3/undefined";
const web3 = new Web3(rpcURL);

export const removeDuplicateObjectFromArray = (arr: object[], key: string) => {
  const check = {};

  const filteredArr = [];

  arr.forEach((obj) => {
    if (!check[obj[key]]) {
      check[obj[key]] = true;

      filteredArr.push(obj);
    }
  });

  return filteredArr;
};

export const getErc20Balance = async (
  walletAddress: string,
  tokenAddress: string
) => {
  const contract = new web3.eth.Contract(getBalanceAbi, tokenAddress);

  const balance = await contract.methods.balanceOf(walletAddress).call();

  return balance;
};
